export const initialState = {
  basket: [],
};

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      //Logic for adding basket
      return { state };
      break;
    case "REMOVE_FROM_BASKET":
      //Logic for removing basket
      return { state };
      break;
    default:
      return state;
  }
};

export default reducer;
